INSERT INTO `infodata` VALUES ('杭州', '拱墅区', '网上传孩子上幼儿园年龄适当放开是否属实，尤其是女孩9月2日出生，相当于4周岁才可以上学，肯请适当放宽', '2019/3/11 00:00:00', '已作出处理意见', '网上传孩子上幼儿园年龄适当放开是否属实，尤其是女孩9月2日出生，相当于4周岁才可以上学，肯请适当放宽', '2019/3/11 00:00:00', '手机APP', '拱墅区教育局', '2019/3/11 00:00:00', '3月11日上午11:19已电话联系该家长，把******幼儿园入园报名政策进行讲解。 联系人：胡老师 联系电话：88*****2');
